# # ps4pr1.py - Problem Set 4, Problem 1## From binary to decimal and back!## Function 1def dec_to_bin(n):    """ return a stirng version of the binary        representation of that number    """    if n == 0:        return '0'    elif n == 1:        return '1'    else:        dec_to_bin_rest = dec_to_bin(n//2)        if n % 2 == 0:            return dec_to_bin_rest + '0'        else:            return dec_to_bin_rest + '1'# Function 2def bin_to_dec(b):    """ return a binary number that converts the        number from binary to decimal    """    if b == '0':      return 0    elif b == '1':      return 1    else:        bin_to_dec_rest = bin_to_dec(b[:-1])        if b[-1] == '1':            return 2 *  bin_to_dec_rest + 1        else:            return 2 *  bin_to_dec_rest 